CREATE DATABASE usermanagement;

CREATE TABLE usermanagement.roles (
	id INT AUTO_INCREMENT PRIMARY KEY NOT NULL,
    role VARCHAR(15) UNIQUE NOT NULL,
	permissions VARCHAR(10) NOT NULL
);


CREATE TABLE usermanagement.accounts (
	account_id INT AUTO_INCREMENT PRIMARY KEY NOT NULL,
	permissions_id INT DEFAULT 3,
	username VARCHAR(50) NOT NULL,
	password VARCHAR(50) NOT NULL,
	first_name VARCHAR(50) NOT NULL,
	last_name VARCHAR(50) NOT NULL,
	email VARCHAR(50) NOT NULL,
	phone VARCHAR(50) NOT NULL,
	comments TEXT,
	date_created DATE NOT NULL,
	status VARCHAR(10) NOT NULL DEFAULT 'active',
	FOREIGN KEY(permissions_id) REFERENCES roles(id)
);

